import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Editor.css';

const Editor = ({ template, setTemplate }) => {
  const [isLoading, setIsLoading] = useState(false); // Loading state
  const [isPreviewVisible, setIsPreviewVisible] = useState(false); // Preview card visibility

  // Fetch the email layout from the backend
  const fetchLayout = async () => {
    setIsLoading(true);
    try {
      const res = await axios.get('http://localhost:5000/api/emails/getEmailLayout');
      console.log(res.data); // Layout HTML, you can display this or process it
    } catch (error) {
      console.error('Error fetching layout:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // Call fetchLayout once when component mounts
  useEffect(() => {
    fetchLayout();
  }, []);

  // Save the template to the backend
  const saveTemplate = async () => {
    setIsLoading(true);
    try {
      await axios.post('http://localhost:5000/api/emails/uploadEmailConfig', template);
      alert('Template saved!');
    } catch (error) {
      console.error('Error saving template:', error);
      alert('Failed to save template.');
    } finally {
      setIsLoading(false);
    }
  };

  // Download the template as HTML
  const downloadTemplate = async () => {
    setIsLoading(true);
    try {
      const res = await axios.post(
        'http://localhost:5000/api/emails/renderAndDownloadTemplate',
        template,
        { responseType: 'blob' }
      );
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'email_template.html');
      document.body.appendChild(link);
      link.click();
    } catch (error) {
      console.error('Error downloading template:', error);
      alert('Failed to download template.');
    } finally {
      setIsLoading(false);
    }
  };

  // Image upload handler
  const handleImageUpload = async (e) => {
    const formData = new FormData();
    formData.append('image', e.target.files[0]); // 'image' matches the backend field name

    try {
      const res = await axios.post('http://localhost:5000/api/emails/uploadImage', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      console.log('Image uploaded:', res.data.imageUrl);
      setTemplate({ ...template, imageUrl: res.data.imageUrl });
    } catch (error) {
      console.error('Image upload failed:', error);
      alert('Image upload failed.');
    }
  };

  // Preview template handler
  const previewTemplate = () => {
    setIsPreviewVisible(true); // Show the preview card
  };

  // Close the preview card
  const closePreview = () => {
    setIsPreviewVisible(false);
  };

  return (
    <div className="editor">
      {/* Title Input */}
      <input
        type="text"
        name="title"
        placeholder="Enter Title"
        value={template.title}
        onChange={(e) => setTemplate({ ...template, title: e.target.value })}
      />

      {/* Content Textarea */}
      <textarea
        name="content"
        placeholder="Enter Content"
        value={template.content}
        onChange={(e) => setTemplate({ ...template, content: e.target.value })}
      />

      {/* Footer Input */}
      <input
        type="text"
        name="footer"
        placeholder="Enter Footer"
        value={template.footer}
        onChange={(e) => setTemplate({ ...template, footer: e.target.value })}
      />

      {/* Image Upload */}
      <input
        type="file"
        onChange={handleImageUpload}
      />

      {/* Buttons Container */}
      <div className="button-container">
        {/* Save Button */}
        <button onClick={saveTemplate} disabled={isLoading} className="save-button">
          {isLoading ? 'Saving...' : 'Save Template'}
        </button>

        {/* Preview Button */}
        <button onClick={previewTemplate} className="preview-button">
          Preview Template
        </button>

        {/* Download Button */}
        <button onClick={downloadTemplate} disabled={isLoading} className="download-button">
          {isLoading ? 'Downloading...' : 'Download Template'}
        </button>
      </div>

      {/* Preview Card */}
      {isPreviewVisible && (
        <div className="preview-card">
          <div className="preview-content">
            <button className="close-preview" onClick={closePreview}>Close Preview</button>
              <h1>{template.title}</h1>
              <p>{template.content}</p>
              <footer>{template.footer}</footer>
              {template.imageUrl && (
                <img src={`http://localhost:5000${template.imageUrl}`} alt="Uploaded" />
              )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Editor;
