import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Editor.css';

const Editor = ({ template, setTemplate }) => {
  const [isLoading, setIsLoading] = useState(false);
  const [isPreviewVisible, setIsPreviewVisible] = useState(false);
  const [activeSection, setActiveSection] = useState('title');
  const [styles, setStyles] = useState({
    title: { color: '#ffffff', fontSize: '24px', textAlign: 'center' },
    content: { color: '#ffffff', fontSize: '18px', textAlign: 'left' },
    footer: { color: '#ffffff', fontSize: '16px', textAlign: 'right' },
  });

  const handleStyleChange = (key, value) => {
    setStyles((prev) => ({
      ...prev,
      [activeSection]: { ...prev[activeSection], [key]: value },
    }));
  };

  const moveSection = (section, direction) => {
    const sections = ['title', 'content', 'footer'];
    const index = sections.indexOf(section);
    const newIndex = direction === 'up' ? index - 1 : index + 1;

    if (newIndex >= 0 && newIndex < sections.length) {
      const newOrder = [...sections];
      [newOrder[index], newOrder[newIndex]] = [newOrder[newIndex], newOrder[index]];

      const newTemplate = {
        title: newOrder[0] === 'title' ? template.title : newOrder[0] === 'content' ? template.content : template.footer,
        content: newOrder[1] === 'title' ? template.title : newOrder[1] === 'content' ? template.content : template.footer,
        footer: newOrder[2] === 'title' ? template.title : newOrder[2] === 'content' ? template.content : template.footer,
      };

      setTemplate(newTemplate);
    }
  };

  const fetchLayout = async () => {
    setIsLoading(true);
    try {
      const res = await axios.get('http://localhost:5000/api/emails/getEmailLayout');
      console.log(res.data); // Layout HTML
    } catch (error) {
      console.error('Error fetching layout:', error);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchLayout();
  }, []);

  const saveTemplate = async () => {
    setIsLoading(true);
    try {
      await axios.post('http://localhost:5000/api/emails/uploadEmailConfig', template);
      alert('Template saved!');
    } catch (error) {
      console.error('Error saving template:', error);
      alert('Failed to save template.');
    } finally {
      setIsLoading(false);
    }
  };

  const downloadTemplate = async () => {
    setIsLoading(true);
    try {
      const res = await axios.post(
        'http://localhost:5000/api/emails/renderAndDownloadTemplate',
        { ...template, styles },
        { responseType: 'blob' }
      );
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'email_template.html');
      document.body.appendChild(link);
      link.click();
    } catch (error) {
      console.error('Error downloading template:', error);
      alert('Failed to download template.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleImageUpload = async (e) => {
    const formData = new FormData();
    formData.append('image', e.target.files[0]);

    try {
      const res = await axios.post('http://localhost:5000/api/emails/uploadImage', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setTemplate({ ...template, imageUrl: res.data.imageUrl });
    } catch (error) {
      console.error('Image upload failed:', error);
      alert('Image upload failed.');
    }
  };

  return (
    <div className="editor-container">
      <div className="editor">
        {/* Inputs with Arrow Controls */}
        <div className="section-wrapper">
          <input
            type="text"
            name="title"
            placeholder="Enter Title"
            value={template.title}
            onChange={(e) => setTemplate({ ...template, title: e.target.value })}
            style={styles.title}
          />
          <div className="arrow-controls">
            <button onClick={() => moveSection('title', 'up')}>&uarr;</button>
            <button onClick={() => moveSection('title', 'down')}>&darr;</button>
          </div>
        </div>

        <div className="section-wrapper">
          <textarea
            name="content"
            placeholder="Enter Content"
            value={template.content}
            onChange={(e) => setTemplate({ ...template, content: e.target.value })}
            style={styles.content}
          />
          <div className="arrow-controls">
            <button onClick={() => moveSection('content', 'up')}>&uarr;</button>
            <button onClick={() => moveSection('content', 'down')}>&darr;</button>
          </div>
        </div>

        <div className="section-wrapper">
          <input
            type="text"
            name="footer"
            placeholder="Enter Footer"
            value={template.footer}
            onChange={(e) => setTemplate({ ...template, footer: e.target.value })}
            style={styles.footer}
          />
          <div className="arrow-controls">
            <button onClick={() => moveSection('footer', 'up')}>&uarr;</button>
            <button onClick={() => moveSection('footer', 'down')}>&darr;</button>
          </div>
        </div>

        <input type="file" onChange={handleImageUpload} />
      </div>

      {/* Control Panel */}
      <div className="control-panel">
        <h3>Edit {activeSection.charAt(0).toUpperCase() + activeSection.slice(1)} Styles</h3>
        <label>
          Color:
          <input
            type="color"
            value={styles[activeSection].color}
            onChange={(e) => handleStyleChange('color', e.target.value)}
          />
        </label>
        <label>
          Font Size:
          <input
            type="number"
            value={parseInt(styles[activeSection].fontSize, 10)}
            onChange={(e) => handleStyleChange('fontSize', `${e.target.value}px`)}
          />
        </label>
        <label>
          Alignment:
          <select
            value={styles[activeSection].textAlign}
            onChange={(e) => handleStyleChange('textAlign', e.target.value)}
          >
            <option value="left">Left</option>
            <option value="center">Center</option>
            <option value="right">Right</option>
          </select>
        </label>
        <div className="section-selector">
          <button onClick={() => setActiveSection('title')}>Title</button>
          <button onClick={() => setActiveSection('content')}>Content</button>
          <button onClick={() => setActiveSection('footer')}>Footer</button>
        </div>
      </div>

      {/* Buttons */}
      <div className="button-container">
        <button onClick={saveTemplate} disabled={isLoading}>
          {isLoading ? 'Saving...' : 'Save Template'}
        </button>
        <button onClick={() => setIsPreviewVisible(true)}>Preview Template</button>
        <button onClick={downloadTemplate} disabled={isLoading}>
          {isLoading ? 'Downloading...' : 'Download Template'}
        </button>
      </div>

      {/* Preview Modal */}
      {isPreviewVisible && (
        <div className="preview-modal">
          <div className="preview-card">
            <button className="close-preview" onClick={() => setIsPreviewVisible(false)}>
              Close Preview
            </button>
            <h1 style={styles.title}>{template.title}</h1>
            <p style={styles.content}>{template.content}</p>
            <footer style={styles.footer}>{template.footer}</footer>
            {template.imageUrl && <img src={`http://localhost:5000${template.imageUrl}`} alt="Uploaded" />}
          </div>
        </div>
      )}
    </div>
  );
};

export default Editor;
